mapper.select:
SELECT dps.id AS product_id, dps.product_name, dps.spu_status, dps.img, dps.product_order, dps.praise, dps.product_describe, dabd.`name`, ddf.freight, dpsk.sku_attr_names, dpsk.sku_attr_value_names, dpsk.sku_price, dpsk.sku_count, dpsk.sku_rate, dab.`name` as fre_name FROM djmall_product_spu dps LEFT JOIN djmall_auth_base_data dabd ON dps.type = dabd.`code` LEFT JOIN djmall_dict_freight ddf ON dps.freight_id = ddf.id LEFT JOIN djmall_product_sku dpsk ON dpsk.product_id = dps.id LEFT JOIN djmall_auth_base_data dab ON ddf.base_code = dab.`code` WHERE dpsk.is_default = 0
and dps.product_name = #{product.productName}
and dps.spu_status = #{product.spuStatus}
and dpsk.sku_price < #{product.skuPriceMax}
and dpsk.sku_price > #{product.skuPriceMin}
and dps.type in
#{type}
and dps.user_id = #{product.userId}
GROUP BY dpsk.product_id

SELECT dps.id as product_id, dps.product_name, dsk.sku_price, dsk.sku_rate, ddf.freight, dps.img, dps.product_describe, dsk.id as product_sku_id, dsk.sku_attr_names, dsk.sku_attr_value_names, dps.praise, dsk.sku_count FROM djmall_product_spu dps LEFT JOIN djmall_product_sku dsk ON dps.id = dsk.product_id LEFT JOIN djmall_dict_freight ddf ON dps.freight_id = ddf.id
dps.id = #{product.productSpuId}
AND dsk.is_default= #{product.isDefault}
AND dsk.id= #{product.productSkuId}
